var components = document.getElementsByClassName('required-input');


function isRequiredInputs() {
    var bool = true;
    for (i = 0; i < components.length; i++) {

        var val = (components[i].value.trim());
        if (val.length == 0) {
            components[i].style.border = "1px solid red";
            components[i].focus();
            bool = false;
            break;
        } else {
            components[i].style.border = "1px solid #e1e1e1";
            components[i].value = val;
        }
    }
    
    return bool;
}












